Item.method_code:
let editorField = getFieldByName('rule_editor');
let editorFrame = editorField.querySelector('iframe');

function addGroupValueEnteredEvent() {
	let editorControl = editorFrame.contentWindow.editorControl;
	let expressionSerializer = editorFrame.contentWindow.expressionSerializer;
	if (!expressionSerializer || !editorControl || editorControl.eventCallbacks.onGroupValueEntered.length === 0) {
		setTimeout(addGroupValueEnteredEvent, 30);
		return;
	}

	if (editorControl.eventCallbacks.onGroupValueEntered.length === 1) {
		editorControl.addEventListener(window, null, 'onGroupValueEntered', function() {
			let definition = document.thisItem.getProperty('definition');
			let expressionStringNotation = expressionSerializer.deserializeExpressionToString(definition);
			document.thisItem.setProperty('expression_string_notation', expressionStringNotation);
		});
	}
}

let isNew = document.item.getAttribute('isTemp') === '1';
if (!isNew) {
	addGroupValueEnteredEvent();
}
